package com.wally.playground.simple_java.base64_to_image;

import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Paths;
import java.util.Base64;

public class Base64ToImageTest {
    public static void main(String[] args) {
        /*
            base64 관련 테스트 가능: https://base64.guru/converter/encode/image
         */
        String base64 = "";
        byte[] decode = Base64.getDecoder().decode(base64);  // base64.getBytes() 이런거 안됨

        try {
            Files.write(Paths.get("image.png"), decode);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

}
